import React from 'react';
import { Navigate } from 'react-router-dom';

const AdminRoute = ({ children }) => {
    const user = JSON.parse(localStorage.getItem('user')); // Retrieve the user data from localStorage

    // Check if user is not logged in or is not an admin
    if (!user || user.role !== 'admin') {
        return <Navigate to="/" />; // Redirect to home page if not an admin
    }

    // If the user is an admin, render the children (the page/route passed to this component)
    return children;
};

export default AdminRoute;
